
public class Compare {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int firstNumber = 25;
        int secondNumber = 39;

        // Comparing the two numbers
        if (firstNumber != secondNumber) {
            System.out.println("First number (" + firstNumber + ") is not equal to second number (" + secondNumber + ").");
        }

        if (firstNumber < secondNumber) {
            System.out.println("First number (" + firstNumber + ") is less than second number (" + secondNumber + ").");
        }

        if (firstNumber <= secondNumber) {
            System.out.println("First number (" + firstNumber + ") is less than or equal to second number (" + secondNumber + ").");
        }
			 



	}

}
