package Loops;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		   
		        int i = 1; // Initialize the counter
		        
		        // While loop to print numbers from 1 to 10
		        while (i <= 10) {
		            System.out.println("Number: " + i);
		            i++; // Increment the counter
		        }
		    }
		


	}


