
public class Operations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int result1 = -5 + 8 * 6;
        int result2 = (55 + 9) % 9;
        int result3 = 20 + -3 * 5 / 8;
        int result4 = 5 + 15 / 3 * 2 - 8 % 3;
        
      
        System.out.println("The result of -5 + 8 * 6 is: " + result1);
        System.out.println("The result of (55 + 9) % 9 is: " + result2);
        System.out.println("The result of 20 + -3 * 5 / 8 is: " + result3);
        System.out.println("The result of 5 + 15 / 3 * 2 - 8 % 3 is: " + result4);


	}

}
